import React, { useEffect, useRef } from 'react';
import 'ol/ol.css';
import { Map, View } from 'ol';
import TileLayer from 'ol/layer/Tile';
import VectorLayer from 'ol/layer/Vector';
import OSM from 'ol/source/OSM';
import VectorSource from 'ol/source/Vector';
import { fromLonLat } from 'ol/proj';
import GeoJSON from 'ol/format/GeoJSON';

const MapView = ({ geoJsonData }) => {
  const mapElement = useRef();
  const map = useRef(null); // Initialize as null

  useEffect(() => {
    if (!map.current) {
      map.current = new Map({
        target: mapElement.current,
        layers: [
          new TileLayer({
            source: new OSM(),
          }),
        ],
        view: new View({
          center: fromLonLat([0, 0]),
          zoom: 2,
        }),
      });
    }
    return () => {
      // Cleanup map on component unmount
      if (map.current) {
        map.current.setTarget(null);
      }
    };
  }, []);

  useEffect(() => {
    if (geoJsonData && map.current) {
      const vectorSource = new VectorSource({
        features: new GeoJSON().readFeatures(geoJsonData, {
          featureProjection: 'EPSG:3857',
        }),
      });
      const vectorLayer = new VectorLayer({
        source: vectorSource,
      });
      map.current.addLayer(vectorLayer);
      map.current.getView().fit(vectorSource.getExtent(), { duration: 1000 });
    }
  }, [geoJsonData]);

  return <div ref={mapElement} style={{ width: '100%', height: '400px' }} />;
};

export default MapView;
