import React, { useState } from 'react';
import './App.css';
import MapView from './MapView';

function App() {
  const [geoJsonData, setGeoJsonData] = useState(null);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        try {
          const data = JSON.parse(reader.result);
          setGeoJsonData(data);
        } catch (error) {
          console.error('Error parsing GeoJSON file', error);
          alert('Error parsing GeoJSON file. Please ensure it is valid.');
        }
      };
      reader.readAsText(file);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <img src={require('./images/logo-chakra.jpg')} alt="Logo" className="logo" />
        <h2>GeoJSON Web Viewer</h2>
      </header>
      <input id="loadfile" name="loadfile" type="file" accept=".geojson" onChange={handleFileChange} />
      <label htmlFor="loadfile">Load GeoJSON</label>
      <div className="map-container">
        <MapView geoJsonData={geoJsonData} />
      </div>
      <footer className="credits">
        <a href="https://www.flaticon.com/free-icons/parchment" title="parchment icons">Parchment icons created by Freepik - Flaticon</a>
      </footer>
      <div id="activity" className="textbox"></div>
    </div>
  );
}

export default App;
